import React, { useState } from 'react';
import './Register.css';

const Register = () => {
    const [form, setForm] = useState({
        nom: '',
        prenom: '',
        email: '',
        motDePasse: ''
    });

    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);
    const [apiError, setApiError] = useState('');

    const validateForm = () => {
        const newErrors = {};
        if (!form.nom.trim()) newErrors.nom = 'Le nom est requis';
        if (!form.prenom.trim()) newErrors.prenom = 'Le prénom est requis';

        if (!form.email.trim()) {
            newErrors.email = "L'email est requis";
        } else if (!/\S+@\S+\.\S+/.test(form.email)) {
            newErrors.email = "L'email n'est pas valide";
        }

        if (!form.motDePasse.trim()) {
            newErrors.motDePasse = 'Le mot de passe est requis';
        } else if (form.motDePasse.length < 8) {
            newErrors.motDePasse = 'Le mot de passe doit contenir au moins 8 caractères';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
        if (errors[name]) setErrors({ ...errors, [name]: '' });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setApiError('');

        if (!validateForm()) return;

        setLoading(true);

        try {
            const response = await fetch('http://localhost:8080/api/auth/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(form)
            });

            const data = await response.text();

            if (!response.ok) throw new Error(data || 'Une erreur est survenue');

            setSuccess(true);
            setForm({ nom: '', prenom: '', email: '', motDePasse: '' });
        } catch (error) {
            setApiError(error.message || 'Une erreur de connexion est survenue');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="bg-white shadow-lg rounded-xl w-full max-w-md p-8">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Créer un compte</h2>

                {success ? (
                    <div className="bg-green-100 text-green-700 p-4 rounded-md text-center">
                        <p className="font-semibold">Inscription réussie !</p>
                        <p className="text-sm">Vous pouvez maintenant vous connecter.</p>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} className="space-y-4">
                        {apiError && (
                            <div className="bg-red-100 text-red-700 p-3 rounded-md text-sm">
                                {apiError}
                            </div>
                        )}

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="nom">Nom</label>
                            <input
                                type="text"
                                id="nom"
                                name="nom"
                                value={form.nom}
                                onChange={handleChange}
                                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.nom ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.nom && <p className="text-sm text-red-600 mt-1">{errors.nom}</p>}
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="prenom">Prénom</label>
                            <input
                                type="text"
                                id="prenom"
                                name="prenom"
                                value={form.prenom}
                                onChange={handleChange}
                                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.prenom ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.prenom && <p className="text-sm text-red-600 mt-1">{errors.prenom}</p>}
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={form.email}
                                onChange={handleChange}
                                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.email ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.email && <p className="text-sm text-red-600 mt-1">{errors.email}</p>}
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="motDePasse">Mot de passe</label>
                            <input
                                type="password"
                                id="motDePasse"
                                name="motDePasse"
                                value={form.motDePasse}
                                onChange={handleChange}
                                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.motDePasse ? 'border-red-500' : 'border-gray-300'}`}
                            />
                            {errors.motDePasse && <p className="text-sm text-red-600 mt-1">{errors.motDePasse}</p>}
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white rounded-md font-semibold text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:bg-blue-300"
                        >
                            {loading ? "Création du compte..." : "S'inscrire"}
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default Register;
