import React, { useState } from 'react';
function Register() {
    const [form, setForm] = useState({ nom: "", prenom: "", email: "", motDePasse: "", confirm: "" });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (form.motDePasse !== form.confirm) return alert("Les mots de passe ne correspondent pas.");

        await fetch("http://localhost:8080/api/auth/register", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(form)
        })
            .then(res => res.text())
            .then(alert);
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Inputs: nom, pr√©nom, email, motDePasse, confirm */}
        </form>
    );
}
