import React, { useState } from 'react';
import PropTypes from 'prop-types';

const FileUploader = ({ onUpload, allowedTypes, maxSizeMB = 10 }) => {
    const [file, setFile] = useState(null);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        
        // Validation du fichier
        if (!selectedFile) {
            setError('Aucun fichier sélectionné');
            return;
        }

        // Vérification du type de fichier
        if (allowedTypes && !allowedTypes.includes(selectedFile.type)) {
            setError(`Type de fichier non supporté. Formats autorisés: ${allowedTypes.join(', ')}`);
            return;
        }

        // Vérification de la taille
        if (selectedFile.size > maxSizeMB * 1024 * 1024) {
            setError(`Fichier trop volumineux (max ${maxSizeMB}MB)`);
            return;
        }

        setFile(selectedFile);
        setError('');
    };

    

    const handleSubmit = async () => {
        if (!file) {
            setError('Veuillez sélectionner un fichier valide');
            return;
        }

        setIsLoading(true);
        setError('');

        try {
            await onUpload(file);
        } catch (err) {
            setError(err.message || 'Échec du téléversement');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="file-uploader-container" style={{ margin: '20px 0' }}>
            <input 
                type="file" 
                onChange={handleFileChange}
                accept={allowedTypes?.join(',')}
                disabled={isLoading}
                style={{ display: 'block', margin: '10px 0' }}
            />
            
            <button 
                onClick={handleSubmit}
                disabled={!file || isLoading}
                style={{
                    padding: '8px 16px',
                    backgroundColor: isLoading ? '#ccc' : '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: !file || isLoading ? 'not-allowed' : 'pointer'
                }}
            >
                {isLoading ? 'Téléversement en cours...' : 'Téléverser'}
            </button>
            
            {error && (
                <p style={{ color: 'red', marginTop: '10px' }}>
                    {error}
                </p>
            )}
        </div>
    );
};

FileUploader.propTypes = {
    onUpload: PropTypes.func.isRequired,
    allowedTypes: PropTypes.arrayOf(PropTypes.string),
    maxSizeMB: PropTypes.number
};

export default FileUploader;
