import React, { useState } from 'react';
import FileUploader from '../components/FileUploader';
import Result from '../components/Result';
import { uploadFile } from '../services/apiService';

const QuizPage = () => {
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null); // Ajout pour gérer les erreurs UI
    const [isLoading, setIsLoading] = useState(false); // Ajout pour un état de chargement

    const handleFileUpload = async (file) => {
        // Validation basique du fichier
        if (!file) {
            setError("Veuillez sélectionner un fichier valide.");
            return;
        }

        setIsLoading(true); // Activer l'état de chargement
        setError(null); // Réinitialiser les erreurs précédentes

        try {
            const data = await uploadFile(file, 'quizes/generate'); // Endpoint corrigé
            setResult(data);
        } catch (error) {
            setError(error.message || "Échec de la génération du quiz. Veuillez réessayer.");
            console.error('Erreur backend:', error.response?.data || error);
        } finally {
            setIsLoading(false); // Désactiver l'état de chargement
        }
    };

    return (
        <div className="quiz-page"> {/* Ajout d'une classe pour le styling */}
            <h1>Générer un Quiz</h1>
            
            {/* Composant FileUploader avec désactivation pendant le chargement */}
            <FileUploader 
                onUpload={handleFileUpload} 
                disabled={isLoading} 
                accept=".pdf,.txt,.docx" // Limiter les types de fichiers (optionnel)
            />
            
            {/* Affichage conditionnel */}
            {isLoading && <p>Génération du quiz en cours...</p>}
            {error && <p className="error-message">{error}</p>}
            {result && <Result data={result} />}
        </div>
    );
};

export default QuizPage;
