import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';

const Dashboard = () => {
    const navigate = useNavigate();

    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);

    const handleChoice = (choice) => {
        navigate(`/${choice}`);
    };

    return (
        <div className="dashboard-container">
            {/* Boutons en haut à droite */}
            <div style={{ position: "absolute", top: 10, right: 20 }}>
                <button onClick={() => setShowLogin(true)} style={{ marginRight: "10px" }}>Connexion</button>
                <button onClick={() => setShowRegister(true)}>Inscription</button>
            </div>

            {/* Titre et boutons */}
            <h1>Choisissez une action</h1>
            <div className="dashboard-buttons">
                <button onClick={() => handleChoice('quiz')}>Quiz</button>
                <button onClick={() => handleChoice('summary')}>Résumé</button>
                <button onClick={() => handleChoice('problem')}>Problème</button>
            </div>

            {/* Formulaires conditionnels */}
            {showLogin && <Login onClose={() => setShowLogin(false)} />}
            {showRegister && <Register onClose={() => setShowRegister(false)} />}
        </div>
    );
};

export default Dashboard;
