import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


const Login = () => {
    const navigate = useNavigate();
    const [form, setForm] = useState({
        email: '',
        motDePasse: ''
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        try {
            console.log("Envoi des données au backend: ", form);

            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(form) // Envoie les données (email, motDePasse)
            });

            // Vérification si la réponse est en JSON ou en texte brut
            const contentType = response.headers.get("content-type");
            let data;
            if (contentType && contentType.includes("application/json")) {
                data = await response.json();
                console.log("Réponse JSON du backend:", data);
            } else {
                const message = await response.text();
                data = { message };
                console.log("Réponse texte du backend:", message);
            }

            if (!response.ok) throw new Error(data.message || "Erreur de connexion");

            // Stockage du token et des informations utilisateur
            localStorage.setItem('userToken', data.token);
            localStorage.setItem('userName', data.userName || data.nom || form.email.split('@')[0]);

            setSuccess("Connexion réussie!");
            setForm({ email: '', motDePasse: '' });

            // Redirection vers le Dashboard après connexion réussie
            setTimeout(() => {
                navigate('/dashboard');
            }, 1000);

        } catch (err) {
            setError(err.message || "Erreur de connexion.");
        }
    };

    return (
        <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow rounded">
            <h2 className="text-xl font-bold mb-4 text-center">Connexion</h2>

            {success && <div className="text-green-600 mb-4">{success}</div>}
            {error && <div className="text-red-600 mb-4">{error}</div>}

            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="email" className="block mb-1 text-sm">Email</label>
                    <input
                        type="email"
                        name="email"
                        id="email"
                        value={form.email}
                        onChange={handleChange}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded"
                    />
                </div>

                <div>
                    <label htmlFor="motDePasse" className="block mb-1 text-sm">Mot de passe</label>
                    <input
                        type="password"
                        name="motDePasse"
                        id="motDePasse"
                        value={form.motDePasse}
                        onChange={handleChange}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded"
                    />
                </div>

                <button
                    type="submit"
                    className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
                >
                    Se connecter
                </button>
            </form>
        </div>
    );
};

export default Login;
