import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080';

export const generateQuiz = async (file, model) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('model', model); // Ajoutez le modèle ici

    try {
        const response = await axios.post(`${API_BASE_URL}/api/quizes/generate`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
        return response.data;
    } catch (error) {
        console.error('Erreur lors de l\'upload du fichier :', error.response?.data || error.message);
        throw error;
    }
};
