import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

const FileUploader = ({ 
  onSuccess, 
  onError,
  maxSizeMB = 10,
  extractText = false,
  apiBaseUrl = 'http://localhost:8080'
}) => {
    const [file, setFile] = useState(null);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [progress, setProgress] = useState(0);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setError('');

        if (!selectedFile) {
            setError('Aucun fichier sélectionné');
            return;
        }

        if (selectedFile.size > maxSizeMB * 1024 * 1024) {
            setError(`Fichier trop volumineux (max ${maxSizeMB}MB)`);
            return;
        }

        setFile(selectedFile);
    };

    const handleSubmit = async () => {
        if (!file) {
            setError('Veuillez sélectionner un fichier valide');
            return;
        }

        setIsLoading(true);
        setError('');

        const formData = new FormData();
        formData.append('file', file);
        if (extractText) {
            formData.append('extractText', 'true');
        }

        try {
            const response = await axios.post(`${apiBaseUrl}/api/uploaded-files/upload`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                },
                onUploadProgress: (progressEvent) => {
                    const percentCompleted = Math.round(
                        (progressEvent.loaded * 100) / progressEvent.total
                    );
                    setProgress(percentCompleted);
                }
            });

            if (onSuccess) {
                onSuccess(response.data);
            }
            
            setFile(null);
            setProgress(0);
            document.getElementById('file-input').value = '';
        } catch (err) {
            const errorMsg = err.response?.data?.message || 
                           err.message || 
                           'Échec du téléversement';
            setError(errorMsg);
            
            if (onError) {
                onError(errorMsg);
            }
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="file-uploader-container" style={{ 
            margin: '20px 0',
            padding: '20px',
            border: '1px dashed #ccc',
            borderRadius: '5px'
        }}>
            <input
                id="file-input"
                type="file"
                onChange={handleFileChange}
                accept="" // Autorise tous les types de fichiers
                disabled={isLoading}
                aria-label="Sélectionner un fichier à téléverser"
                style={{ display: 'block', margin: '10px 0' }}
            />

            {file && (
                <div style={{ margin: '10px 0' }}>
                    <p>Fichier sélectionné: {file.name}</p>
                    <p>Taille: {(file.size / (1024 * 1024)).toFixed(2)} MB</p>
                </div>
            )}

            {isLoading && (
                <div style={{ width: '100%', backgroundColor: '#f1f1f1', margin: '10px 0' }}>
                    <div 
                        style={{ 
                            width: `${progress}%`,
                            height: '20px',
                            backgroundColor: progress === 100 ? '#4CAF50' : '#2196F3',
                            textAlign: 'center',
                            lineHeight: '20px',
                            color: 'white'
                        }}
                    >
                        {progress}%
                    </div>
                </div>
            )}

            <button
                onClick={handleSubmit}
                disabled={!file || isLoading}
                style={{
                    padding: '10px 20px',
                    backgroundColor: !file || isLoading ? '#cccccc' : '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: !file || isLoading ? 'not-allowed' : 'pointer',
                    fontSize: '16px'
                }}
                aria-disabled={!file || isLoading}
            >
                {isLoading ? 'Téléversement en cours...' : 'Téléverser le fichier'}
            </button>

            {error && (
                <p style={{ 
                    color: '#d32f2f', 
                    marginTop: '10px',
                    padding: '10px',
                    backgroundColor: '#ffebee',
                    borderRadius: '4px'
                }}>
                    {error}
                </p>
            )}
        </div>
    );
};

FileUploader.propTypes = {
    onSuccess: PropTypes.func,
    onError: PropTypes.func,
    maxSizeMB: PropTypes.number,
    extractText: PropTypes.bool,
    apiBaseUrl: PropTypes.string
};

export default FileUploader;
