import React, { useState } from 'react';

function Register() {
    const [form, setForm] = useState({ nom: "", prenom: "", email: "", motDePasse: "", confirm: "" });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (form.motDePasse !== form.confirm) return alert("Les mots de passe ne correspondent pas.");

        await fetch("http://localhost:8080/api/auth/register", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(form)
        })
            .then(res => res.text())
            .then(alert)
            .catch(error => alert("Une erreur est survenue: " + error.message));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm(prevForm => ({
            ...prevForm,
            [name]: value
        }));
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="nom">Nom</label>
                    <input
                        type="text"
                        id="nom"
                        name="nom"
                        value={form.nom}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="prenom">Pr√©nom</label>
                    <input
                        type="text"
                        id="prenom"
                        name="prenom"
                        value={form.prenom}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={form.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="motDePasse">Mot de passe</label>
                    <input
                        type="password"
                        id="motDePasse"
                        name="motDePasse"
                        value={form.motDePasse}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="confirm">Confirmer le mot de passe</label>
                    <input
                        type="password"
                        id="confirm"
                        name="confirm"
                        value={form.confirm}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">S'inscrire</button>
            </form>
        </div>
    );
}

export default Register;
